dashboardQueries.query:
metadata.vendor_name="Corelight"
metadata.product_event_type = "conn" 
observer.hostname!=""
principal.ip!=""
target.ip!=""
$country_len=strings.length(principal.ip_geo_artifact.location.country_or_region)
$host_len=strings.length(principal.ip_geo_artifact.network.dns_domain)
($host_len=0 AND $country_len=0) OR $host_len > 0
$service=about.labels["service"]
$is_broadcast=if(principal.ip="0.0.0.0" OR principal.ip="255.255.255.255" OR target.ip="255.255.255.255" OR target.ip="0.0.0.0","true","false")
$service!=""
$is_broadcast!="true"
$direction=if(about.labels["local_orig"]="true" AND about.labels["local_resp"]="true", "internal", 
           if(about.labels["local_orig"]="true" and about.labels["local_resp"]="false", "outbound",
           if(about.labels["local_orig"]="false" and about.labels["local_resp"]="false", "external", 
           if(about.labels["local_orig"]="false" and about.labels["local_resp"]="true", "inbound", "unknown"
           ))))
$direction="inbound"
$protocol_string=if(network.ip_protocol=88, "EIGRP",
if(network.ip_protocol=50, "ESP",
if(network.ip_protocol=97, "ETHERIP",
if(network.ip_protocol=47, "GRE",
if(network.ip_protocol=1, "ICMP",
if(network.ip_protocol=58, "ICMP6",
if(network.ip_protocol=2, "IGMP",
if(network.ip_protocol=41, "IP6IN4",
if(network.ip_protocol=103, "PIM",
if(network.ip_protocol=132, "SCTP",
if(network.ip_protocol=6, "TCP",
if(network.ip_protocol=17, "UDP",
if(network.ip_protocol=0, "UNKNOWN_IP_PROTOCOL",
if(network.ip_protocol=112, "VRRP", "UNKNOWN_IP_PROTOCOL"))))))))))))))
$hostname=if(principal.ip_geo_artifact.network.dns_domain!="", principal.ip_geo_artifact.network.dns_domain, "Unknown")
match:
    principal.ip, target.ip, $hostname,principal.ip_geo_artifact.location.country_or_region
outcome:
    $proto=array_distinct($protocol_string)
    $orig_byte_sum=sum(network.sent_bytes)
order:
    $orig_byte_sum desc 
limit:
    10

